import { promises as fs } from 'fs';

async function saveData(data: unknown, path: string) {
  try {
    // Ensure directory exists
    await fs.mkdir(dirname(path), { recursive: true });

// Write file
    await fs.writeFile(path, JSON.stringify(data, null, 2));

    return { success: true };
